#!/usr/bin/env python3# Exercise: Presidents# Write a program to:# (1) Load the data from presidents.txt into a dictionary.# (2) Print the years the greatest and least number of presidents were alive.#     (between 1732 and 2016 (inclusive))#     Ex.#       'least = 2015'#       'John Doe'#       'most = 2015'#       'John Doe, Jane Doe, John Adams, and Jane Adams'# Bonus: Confirm there are no ties. If there is a tie print like so:#     Ex.#       'least = 1900, 2013-2015'#       'John Doe (1900)'#       'Jane Doe (2013-2015)'#       'most = 1900-1934, 2013'#       'John Doe, Jane Doe, John Adams, and Jane Adams (1900-1933)'#       'Sally Doe, Billy Doe, Mary Doe, and Cary Doe (1934)'#       'Alice Doe, Bob Doe, Zane Doe, and Yi Do (2013)'############################################################################### Importsimport itertools# Bodydef print_pres():	pres_list = dict()	with open("presidents.txt","r") as pres_file:		for lines in pres_file:			values_read = lines.split(",")			values_read[-1] = values_read[-1].replace("\n","")			pres_list[values_read[0]] = values_read[1:]	least_yr = 1732	most_yr = 1732	least_ct = 0	most_ct = 0	for yr in range(1732,2017):		yr_count = count_pres(pres_list,yr)		if yr_count > most_ct:			most_ct = yr_count			most_yr = yr		if yr_count < least_ct:			least_ct = yr_count			least_yr = yr	print("least = ",least_yr)	print("most = ",most_yr)	##############################################################################def count_pres(pres_list, year):	count = 0	for values in pres_list.values():		if values[1] == "None":			values[1] = 2017		if int(values[0]) <= year <= int(values[1]):			count += 1	return countdef main():  # CALL YOUR FUNCTION BELOW	print_pres()if __name__ == '__main__':	main()